mapper.update:
UPDATE recharge_sys_node
parent_id = #{ParentId}, parent_tree = #{ParentTree}, icon = #{Icon}, sort = #{Sort}, `name` = #{Name}, url = #{Url}, lang_cn = #{LangCn}, lang_tw = #{LangTw}, lang_us = #{LangUs}, description=#{Description}
where id = #{id}

mapper.select:
SELECT

FROM recharge_sys_node t

WHERE t.id = #{id}
